    set(key, value, ttl, tag) {

        if (this.#cache.size >= this.#options.maxSize && !this.#cache.has(key)) {
            this.#evictOldest();
        }


        this.#clearTimer(key);

        const actualTTL = ttl ?? this.#options.defaultTTL;
        const now = Date.now();


        const entry = {
            value,
            expiry: now + actualTTL,
            created: now,
            tag
        };

        this.#cache.set(key, entry);


        if (actualTTL > 0) {
            const timer = setTimeout(() => {
                this.delete(key);
            }, actualTTL);


            if (timer.unref) {
                timer.unref();
            }

            this.#timers.set(key, timer);
        }

        return true;
    }


    get(key) {
        const entry = this.#cache.get(key);

        if (!entry) {
            this.#stats.misses++;
            return undefined;
        }


        if (Date.now() > entry.expiry) {
            this.#stats.misses++;
            this.#stats.expired++;
            this.delete(key);
            return undefined;
        }

        this.#stats.hits++;
        return entry.value;
    }


    has(key) {
        const entry = this.#cache.get(key);

        if (!entry) {
            return false;
        }


        if (Date.now() > entry.expiry) {
            this.delete(key);
            return false;
        }

        return true;
    }


    delete(key) {
        this.#clearTimer(key);
        return this.#cache.delete(key);
